import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import "./edit.css";
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Edit = () => {

  const users = {
    fname: "",
    lname: "",
    email: ""
  }

  const { id } = useParams();

  const [user, setUser] = useState(users);

  const inputChangeHandler = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  }
  
  useEffect( () => {
    axios.get(`http://localhost:8000/api/getone/${id}`)
    .then( (response))

  })



  return (
    <div className='addUser'>

      <Link to={"/"}><i class="fa-solid fa-arrow-left"></i></Link>
      <h3>Edit User Info</h3>
      <form className='addUserForm'>
        <div className="inputGroup">
          <label htmlFor="fname">First Name</label>
          <input type="text" onChange={inputChangeHandler} id="fname" name="fname" autoComplete='off' placeholder='First Name' required />
        </div>

        <div className="inputGroup">
          <label htmlFor="lname">Last Name</label>
          <input type="text" onChange={inputChangeHandler} id="lname" name="lname" autoComplete='off' placeholder='Last Name' required />
        </div>

        <div className="inputGroup">
          <label htmlFor="fname">Email</label>
          <input type="text" onChange={inputChangeHandler} id="email" name="email" autoComplete='off' placeholder='Email' required />
        </div>



        <div className="inputGroup">
          <button type='submit' >Update Info</button>
        </div>
      </form>
    </div>
  )
}

export default Edit
