          }


          <tr>
            <td>1.</td>
            <td>Sajal Namdeo</td>
            <td>sajalnamdeo627@gmail.com</td>
            <td className='actionButtons'>
              <button><i className="fa-solid fa-trash"></i></button>
              <Link to={'/edit'} ><i class="fa-solid fa-user-pen"></i></Link>
            </td>
          </tr>
        </tbody>
      </table>


    </div>
  )
}

export default User
